import './App.css';
import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import NavBar from './components/NavBar';
import Box from '@material-ui/core/Box';
import Icon from '@material-ui/core/Icon';
import { createMuiTheme } from '@material-ui/core/styles';
import { ThemeProvider } from '@material-ui/styles';
import {UnControlled as CodeMirror} from 'react-codemirror2';
import 'codemirror/lib/codemirror.css';
import 'codemirror/theme/material.css';
import Divider from '@material-ui/core/Divider';
import clsx from 'clsx';
import SwipeableDrawer from '@material-ui/core/SwipeableDrawer';
require('codemirror/mode/commonlisp/commonlisp');

//import Popup from 'react-popup';
//require('codemirror/mode/python/python');
//require('codemirror/mode/javascript/javascript');
//import TextField from '@material-ui/core/TextField';

const theme = createMuiTheme({
  palette: {
    primary: {
      main: '#34a0a4',
    },
    secondary: {
      main: '#457eac',
    },
  },
});


const buttonrun = makeStyles((theme) => ({
  root: {
    '& > *': {
      margin: theme.spacing(1),
      color:"white",
      background: "#52b69a"
      //top: 15,
      //width: '16ch',
    },
  },
}));

const buttonin = makeStyles((theme) => ({
  root: {
    '& > *': {
      margin: theme.spacing(1),
      color:"white",
      background: "#34a0a4"
      //top: 15,
      //width: '16ch',
    },
  },
}));

const buttonout = makeStyles((theme) => ({
  root: {
    '& > *': {
      margin: theme.spacing(1),
      color:"white",
      background: "#168aad"
      //top: 15,
      //width: '16ch',
    },
  },
}));

const buttondoc = makeStyles((theme) => ({
  root: {
    '& > *': {
      margin: theme.spacing(1),
      color:"white",
      background: "#1a759f"
      //top: 15,
      //width: '16ch',
    },
  },
}));

const useStyles = makeStyles({
  list: {
    width: 250,
  },
  fullList: {
    width: 'auto',
  },
});


function App() {

  const classesm = useStyles();
const [state, setState] = React.useState({
  top: false,
  bottom:false
});

const toggleDrawer = (anchor, open) => (event) => {
  if (event && event.type === 'keydown' && (event.key === 'Tab' || event.key === 'Shift')) {
    return;
  }

  setState({ ...state, [anchor]: open });
};

const list = (anchor) => (
  <div
    className={clsx(classesm.list, {
      [classesm.fullList]: anchor === 'top' || anchor === 'bottom',
    })}
    role="presentation"
    onClick={toggleDrawer(anchor, false)}
    onKeyDown={toggleDrawer(anchor, false)}
  >

    <Box display="flex" flexDirection="row" p={0} m={0} bgcolor="#eff1f3">
          <Box p={1} >
            <div className={classesr.root}>
            <Button
              variant="contained" 
              size='small'
              color="secondary"
              onClick={() => sendApiRequest()} 
              endIcon={<Icon>play_circle</Icon>}
              >
              Run
              </Button>
            </div>
          </Box>


          <Box p={1} >
            <div className={classesi.root}>
            <Button 
              variant="contained" 
              size='small' 
              color="primary" 
              onClick={() => downloadTxtFile("input")} 
              endIcon={<Icon>file_download</Icon>}
              >
              Download Code
              </Button>
            </div>
          </Box>

          <Box p={1} >
            <div className={classeso.root}>
            <Button
              variant="contained" 
              size='small' 
              color="primary" 
              onClick={() => downloadTxtFile("output")} 
              endIcon={<Icon>send_and_archive</Icon>}
              >
              Download output
              </Button>
            </div>
          </Box>

          <Box p={1} >
            <div className={classesd.root}>
            <Button 
              variant="contained" 
              size= 'small' 
              color="primary" 
              //onClick={() => sendApiRequest()} 
              endIcon={<Icon>sim_card_download</Icon>}
              href='https://github.com/fm-polimi/zot/raw/master/doc/zot-man.pdf'
              >
              Docs
              </Button>
            </div>
          </Box>
          </Box>
        <div>
        </div>

  </div>
);



  const classesr = buttonrun();
  const classesi = buttonin();
  const classeso = buttonout();
  const classesd = buttondoc();
  // eslint-disable-next-line
  const [value, setValue] = React.useState('Controlled');
  const [inputcode, setInputCode] = React.useState();
  const [outputcode, setOutputCode] = React.useState();

  // eslint-disable-next-line
  const handleChange = (event) => {
    setValue(event.target.value);
  };

   function sendApiRequest() {
    const requestOptions = {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ cmd: inputcode })
      };
    fetch('http://192.168.1.7:5000/listhellow/', requestOptions)
    .then(response => response.json())
    .then(data => setOutputCode(data.output));

    //setOutputCode(inputcode);
  }

  function downloadTxtFile(mode){
    const element = document.createElement("a");
    var textoption="";
    var filename="";
    if(mode === "input"){
      textoption = inputcode;
      filename = "zotcode.lisp";
    }
    else{
      textoption = outputcode;
      filename = "zotoutput.txt";
    }
    const file = new Blob([textoption],    
                {type: 'text/plain;charset=utf-8'});
    element.href = URL.createObjectURL(file);
    element.download = filename;
    document.body.appendChild(element);
    element.click();
  }


  return (
    <html lang="en">
    <div>
      <ThemeProvider theme={theme}>
      <link rel="stylesheet" href="https://fonts.googleapis.com/icon?family=Material+Icons" />
      <div>
        <NavBar />
      </div>

      <div>
      {['bottom'].map((anchor) => (
        <React.Fragment key={anchor}>
          <Button onClick={toggleDrawer(anchor, true)}>{anchor}</Button>
          <SwipeableDrawer
            anchor={anchor}
            open={state[anchor]}
            onClose={toggleDrawer(anchor, false)}
            onOpen={toggleDrawer(anchor, true)}
          >
            {list(anchor)}
          </SwipeableDrawer>
        </React.Fragment>
      ))}
    </div>

    <CodeMirror
        value=';Input Code Here'
        options={{
          mode: 'commonlisp',
          theme: 'material',
          lineNumbers: true
        }}
        onChange={(editor, data, value) => setInputCode(value) }
  />

  <div class="div-sep">
  <Divider />
  <Divider />
  </div>
    <CodeMirror
        value={outputcode}
        options={{
          mode: 'xml',
          theme: 'material',
          lineNumbers: true
        }}
        onChange={(editor, data, value) => {} }
    />

    </ThemeProvider>
  </div>
  </html>
  );
}

export default App;
