import AppBar from '@material-ui/core/AppBar'
import Toolbar from '@material-ui/core/Toolbar'
import Typography from '@material-ui/core/Typography'
import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import Box from '@material-ui/core/Box';
import Icon from '@material-ui/core/Icon';
import { createMuiTheme } from '@material-ui/core/styles';
import { ThemeProvider } from '@material-ui/styles';
import {UnControlled as CodeMirror} from 'react-codemirror2';
import clsx from 'clsx';
import Drawer from '@material-ui/core/Drawer';
import IconButton from '@material-ui/core/IconButton';
import Snackbar from '@material-ui/core/Snackbar';
import MuiAlert from '@material-ui/lab/Alert';
import KeyboardEventHandler from 'react-keyboard-event-handler';
import Dialog from '@material-ui/core/Dialog';
import MuiDialogTitle from '@material-ui/core/DialogTitle';
import MuiDialogContent from '@material-ui/core/DialogContent';
import MuiDialogActions from '@material-ui/core/DialogActions';
import { withStyles } from '@material-ui/core/styles';
import CloseIcon from '@material-ui/icons/Close';
import GitHubIcon from '@material-ui/icons/GitHub';
import Accordion from '@material-ui/core/Accordion';
import AccordionSummary from '@material-ui/core/AccordionSummary';
import AccordionDetails from '@material-ui/core/AccordionDetails';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import './App.css';
import 'codemirror/lib/codemirror.css';
import 'codemirror/theme/material.css';
import 'codemirror/theme/eclipse.css';
require('codemirror/mode/commonlisp/commonlisp');
require('codemirror/mode/xml/xml');


const theme = createMuiTheme({
  palette: {
    primary: {
      main: '#34a0a4',
    },
    secondary: {
      main: '#457eac',
    },
  },
});

const stylesDialog = (theme) => ({
  root: {
    margin: 0,
    padding: theme.spacing(2),
    color: "#FFFFFF",
    backgroundColor:"#34a0a4",
  },
  closeButton: {
    position: 'absolute',
    right: theme.spacing(1),
    top: theme.spacing(1),
    color:"#FFFFFF",
  },
});

const DialogTitle = withStyles(stylesDialog)((props) => {
  const { children, classes, onClose, ...other } = props;
  return (
    <MuiDialogTitle disableTypography className={classes.root} {...other}>
      <Typography variant="h6">{children}</Typography>
      {onClose ? (
        <IconButton aria-label="close" className={classes.closeButton} onClick={onClose}>
          <CloseIcon />
        </IconButton>
      ) : null}
    </MuiDialogTitle>
  );
});

const DialogContent = withStyles((theme) => ({
  root: {
    padding: theme.spacing(2),
    color : "#cfd8dc",
    backgroundColor : "#263238"
  },
}))(MuiDialogContent);

const DialogActions = withStyles((theme) => ({
  root: {
    margin: 0,
    padding: theme.spacing(1),
    backgroundColor : "#34a0a4"
  },
}))(MuiDialogActions);

const buttonrun = makeStyles((theme) => ({
  root: {
    '& > *': {
      margin: theme.spacing(1),
      color:"white",
      background: "#40916c"
    },
  },
}));

const buttonin = makeStyles((theme) => ({
  root: {
    '& > *': {
      margin: theme.spacing(1),
      color:"white",
      background: "#34a0a4"
    },
  },
}));

const buttonout = makeStyles((theme) => ({
  root: {
    '& > *': {
      margin: theme.spacing(1),
      color:"white",
      background: "#168aad"
    },
  },
}));

const buttondoc = makeStyles((theme) => ({
  root: {
    '& > *': {
      margin: theme.spacing(1),
      color:"white",
      background: "#2d3142"
    },
  },

}));

const buttonpin = makeStyles((theme) => ({
  root: {
    '& > *': {
      margin: theme.spacing(1),
      color:"white",
      background: "#1e6091"
    },
  },

}));

const buttonshort = makeStyles((theme) => ({
  root: {
    '& > *': {
      margin: theme.spacing(1),
      color:"white",
      background: "#184e77"
    },
  },

}));

const buttondom = makeStyles((theme) => ({
  root: {
    '& > *': {
      margin: theme.spacing(1),
      color:"white",
    },
  },
}));

const useStyles = makeStyles({
  list: {
    width: 100,
  },
  fullList: {
    width: 'fit-content',
    minWidth: "100vw",
  },
});

const useStylesalert = makeStyles((theme) => ({
  root: {
    width: '100%',
    '& > * + *': {
      marginTop: theme.spacing(2),
    },
  },
}));

const useStylesAccordion = makeStyles((theme) => ({
  root: {
    width: '100%',
  },
  heading: {
    fontSize: theme.typography.pxToRem(15),
    fontWeight: theme.typography.fontWeightRegular,
  },
}));

function Alert(props) {
  return <MuiAlert elevation={6} variant="filled" {...props} />;
}

function App() {

  const classesalert = useStylesalert();
  const [warnopen, setOpenwarn] = React.useState(false);
  const [erroropen, setOpenerror] = React.useState(false);
  const [successopen, setOpensuccess] = React.useState(false);

  const handleClosewarn = (event, reason) => {
    if (reason === 'clickaway') {
      return;
    }
    setOpenwarn(false);
  };

  const handleCloseerror = (event, reason) => {
    if (reason === 'clickaway') {
      return;
    }
    setOpenerror(false);
  };

  const handleClosesuccess = (event, reason) => {
    if (reason === 'clickaway') {
      return;
    }
    setOpensuccess(false);
  };

  const classesm = useStyles();
  const [state, setState] = React.useState({
    top: false,
    bottom:false
  });

const toggleDrawer = (anchor, open) => (event) => {
  if (event && event.type === 'keydown' && (event.key === 'Tab' || event.key === 'Shift')) {
    return;
  }

  setState({ ...state, [anchor]: open });

  };

  const list = (anchor) => (
    <div
      className={clsx(classesm.list, {[classesm.fullList]:anchor === 'bottom',})}
      role="presentation"
      onClick={toggleDrawer(anchor, false)}
      onKeyDown={toggleDrawer(anchor, false)}
    >

      <Box display="flex" flexDirection="row" p={0} m={0} bgcolor="#a4b3b6">
            <Box p={1} >
              <div className={classesr.root}>
              <Button
                id="runbutton"
                variant="contained" 
                size='medium'
                color="secondary"
                onClick={() => sendApiRequest()} 
                endIcon={<Icon>play_circle</Icon>}
                >
                Run
                </Button>
              </div>
            </Box>
            <Box p={1} >
              <div className={classesi.root}>
              <Button 
                variant="contained" 
                size='medium' 
                color="primary" 
                onClick={() => downloadTxtFile("input")} 
                endIcon={<Icon>file_download</Icon>}
                >
                Download Code
                </Button>
              </div>
            </Box>

            <Box p={1} >
              <div className={classeso.root}>
              <Button
                variant="contained" 
                size='medium' 
                color="primary" 
                onClick={() => downloadTxtFile("output")} 
                endIcon={<Icon>send_and_archive</Icon>}
                >
                Download output
                </Button>
              </div>
            </Box>

            <Box p={1} >
              <div className={classesp.root}>
