import './App.css';
import AppBar from '@material-ui/core/AppBar'
import Toolbar from '@material-ui/core/Toolbar'
import Typography from '@material-ui/core/Typography'
import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import Box from '@material-ui/core/Box';
import Icon from '@material-ui/core/Icon';
import { createMuiTheme } from '@material-ui/core/styles';
import { ThemeProvider } from '@material-ui/styles';
import {UnControlled as CodeMirror} from 'react-codemirror2';
import 'codemirror/lib/codemirror.css';
import 'codemirror/theme/material.css';
import Divider from '@material-ui/core/Divider';
import clsx from 'clsx';
import Drawer from '@material-ui/core/Drawer';
import IconButton from '@material-ui/core/IconButton';
require('codemirror/mode/commonlisp/commonlisp');
require('codemirror/mode/xml/xml');

//import Popup from 'react-popup';
//require('codemirror/mode/python/python');
//require('codemirror/mode/javascript/javascript');
//import TextField from '@material-ui/core/TextField';

const theme = createMuiTheme({
  palette: {
    primary: {
      main: '#34a0a4',
    },
    secondary: {
      main: '#457eac',
    },
  },
});


const buttonrun = makeStyles((theme) => ({
  root: {
    '& > *': {
      margin: theme.spacing(1),
      color:"white",
      background: "#52b69a"
      //top: 15,
      //width: '16ch',
    },
  },
}));

const buttonin = makeStyles((theme) => ({
  root: {
    '& > *': {
      margin: theme.spacing(1),
      color:"white",
      background: "#34a0a4"
      //top: 15,
      //width: '16ch',
    },
  },
}));

const buttonout = makeStyles((theme) => ({
  root: {
    '& > *': {
      margin: theme.spacing(1),
      color:"white",
      background: "#168aad"
      //top: 15,
      //width: '16ch',
    },
  },
}));

const buttondoc = makeStyles((theme) => ({
  root: {
    '& > *': {
      margin: theme.spacing(1),
      color:"white",
      background: "#1a759f"
      //top: 15,
      //width: '16ch',
    },
  },

}));const buttondom = makeStyles((theme) => ({
  root: {
    '& > *': {
      margin: theme.spacing(1),
      color:"white",
      //background: "#1a759f"
      //top: 15,
      //width: '16ch',
    },
  },
}));

const useStyles = makeStyles({
  list: {
    width: 100,
  },
  fullList: {
    width: 'auto',
  },
});


function App() {

const classesm = useStyles();
const [state, setState] = React.useState({
  top: false,
  bottom:false
});

const toggleDrawer = (anchor, open) => (event) => {
  if (event && event.type === 'keydown' && (event.key === 'Tab' || event.key === 'Shift')) {
    return;
  }

  setState({ ...state, [anchor]: open });
};

const list = (anchor) => (
  <div
    className={clsx(classesm.list, {[classesm.fullList]:anchor === 'bottom',})}
    role="presentation"
    onClick={toggleDrawer(anchor, false)}
    onKeyDown={toggleDrawer(anchor, false)}
  >

    <Box display="flex" flexDirection="row" p={0} m={0} bgcolor="#ced4da">
          <Box p={1} >
            <div className={classesr.root}>
            <Button
              variant="contained" 
              size='medium'
              color="secondary"
              onClick={() => sendApiRequest()} 
              endIcon={<Icon>play_circle</Icon>}
              >
              Run
              </Button>
            </div>
          </Box>
          <Box p={1} >
            <div className={classesi.root}>
            <Button 
              variant="contained" 
              size='medium' 
              color="primary" 
              onClick={() => downloadTxtFile("input")} 
              endIcon={<Icon>file_download</Icon>}
              >
              Download Code
              </Button>
            </div>
          </Box>

          <Box p={1} >
            <div className={classeso.root}>
            <Button
              variant="contained" 
              size='medium' 
              color="primary" 
              onClick={() => downloadTxtFile("output")} 
              endIcon={<Icon>send_and_archive</Icon>}
              >
              Download output
              </Button>
            </div>
          </Box>

          <Box p={1} >
            <div className={classesd.root}>
            <Button 
              variant="contained" 
              size= 'medium' 
              color="primary" 
              endIcon={<Icon>sim_card_download</Icon>}
              href='https://github.com/fm-polimi/zot/raw/master/doc/zot-man.pdf'
              >
              Docs
              </Button>
            </div>
          </Box>
          </Box>
        <div>
        </div>

  </div>
);

  const classesr = buttonrun();
  const classesi = buttonin();
  const classeso = buttonout();
  const classesd = buttondoc();
  const classesdrawer = buttondom();
  // eslint-disable-next-line
  const [value, setValue] = React.useState('Controlled');
  const [inputcode, setInputCode] = React.useState();
  const [outputcode, setOutputCode] = React.useState();

  // eslint-disable-next-line
  const handleChange = (event) => {
    setValue(event.target.value);
  };

   function sendApiRequest() {
    const requestOptions = {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ cmd: inputcode })
      };
    fetch('http://192.168.1.7:5000/listhellow/', requestOptions)
    .then(response => response.json())
    .then(data => setOutputCode(data.output));

    //setOutputCode(inputcode);
  }

  function downloadTxtFile(mode){
    const element = document.createElement("a");
    var textoption="";
    var filename="";
    if(mode === "input"){
      textoption = inputcode;
      filename = "zotCode.lisp";
    }
    else{
      textoption = outputcode;
      filename = "zotOutput.txt";
    }
    const file = new Blob([textoption],    
                {type: 'text/plain;charset=utf-8'});
    element.href = URL.createObjectURL(file);
    element.download = filename;
    document.body.appendChild(element);
    element.click();
  }


  return (
    <html lang="en">
    <div>
      <ThemeProvider theme={theme}>
      <link rel="stylesheet" href="https://fonts.googleapis.com/icon?family=Material+Icons" />
      <div>
        <AppBar position="static" color='primary'>
            <Toolbar>
                <div>
                  {['bottom'].map((anchor) => (
                  <React.Fragment key={anchor}>
                    <div className={classesdrawer.root}>
                      <IconButton 
                      size="medium"
                      onClick={toggleDrawer(anchor, true)}
                      aria-label="Open Run Me"
                      component="span">
                      <Icon>open_in_browser</Icon>
                      </IconButton>
                    </div>
                    <Drawer anchor={anchor} open={state[anchor]} onClose={toggleDrawer(anchor, false)}>
                      {list(anchor)}
                    </Drawer>
                  </React.Fragment>
                  ))}
                </div>
                <Typography variant="h2" color="inherit" align="right" >
                eeZot UI
                </Typography>
            </Toolbar>
        </AppBar>
        </div>

    <CodeMirror
        value=';Input Code Here'
        options={{
          mode: 'commonlisp',
          theme: 'material',
          lineNumbers: true
        }}
        onChange={(editor, data, value) => setInputCode(value) }
  />

  <Divider />
  <Divider />
  <div class="div-sep">

  </div>
    <CodeMirror
        value={outputcode}
        options={{
          mode: 'plaintext',
          theme: 'material',
          lineNumbers: false
        }}
        onChange={(editor, data, value) => {} }
    />

    </ThemeProvider>
  </div>
  </html>
  );
}

export default App;
