import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import { Component } from 'react'
import NavBar from './components/NavBar'
import { RefreshSharp } from '@material-ui/icons';

const useStyles = makeStyles((theme) => ({
  root: {
    '& .MuiTextField-root': {
      margin: theme.spacing(1),
      width: '60ch',
      bottom: -30
      //background: 'linear-gradient(45deg, #FE6B8B 30%, #FF8E53 90%)',
    },
  },
}));

const useStyles1 = makeStyles((theme) => ({
  root: {
    '& .MuiTextField-root': {
      margin: theme.spacing(1),
      width: '60ch',
      bottom: 39,
      left: 700
      //background: 'linear-gradient(45deg, #FE6B8B 30%, #FF8E53 90%)',
    },
  },
}));

const useStylesbutton = makeStyles((theme) => ({
  root: {
    '& > *': {
      margin: theme.spacing(1),
      left: 565,
      bottom: 100,
      width: '12ch',
    },
  },
}));

// sendApiReaquest = () =>{
//   let postdata = {cmd : refs.inputcode.value };
//   let testdata = refs.inputcode.value;
//   refs.outputcode.value = testdata;

//   // fetch('https://localhost:5000/api/Employee',{
//   //   method: 'POST',
//   //   headers:{'Content-type':'application/json'},
//   //     body: empInfo
//   // }).then(r=>r.json()).then(res=>{
//   //   if(res){
//   //     this.setState({message:'New Employee is Created Successfully'});
//   //   }
//   // });


// }

function App() {

  const classes = useStyles();
  const classes1 = useStyles1();
  const classes2 = useStylesbutton();
  const [value, setValue] = React.useState('Controlled');
  const [inputvalue, setInputValue] = React.useState();
  const [outputvalue, setOuputValue] = React.useState();
  const handleChange = (event) => {
    setValue(event.target.value);
  };

  function sendApiRequest() {
    console.log( JSON.stringify({ cmd: {inputvalue} }))
    const requestOptions = {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ cmd: {inputvalue} })
  };
    fetch('http://192.168.1.7:5000/listhellow/', requestOptions)
    .then(response => response.json())
    .then(data => setOuputValue(data.output));

    //setOuputValue(inputvalue);

  }

  return (
    <div>
      <div>
        <NavBar />
      </div>
      <form className={classes.root} noValidate autoComplete="off">
      <TextField
      id="filled-textarea"
      label="Input Code"
      placeholder="ZOT Code"
      multiline
      variant="filled"
      type= "text"
      value={inputvalue} 
			onChange={(e) => setInputValue(e.target.value)}
      />
    </form>

    <form className={classes1.root} noValidate autoComplete="off" >
    <TextField
    id="filled-textarea1"
    //disabled={true}
    InputLabelProps={{ shrink: true }}
    label = "Output Code"
    //placeholder="Placeholder 1"
    multiline
    variant="filled"
    type="text"
    value={outputvalue} 
    onChange={(e) => setOuputValue(e.target.value)}
    />
    </form>

    <div className={classes2.root}>
    <Button variant="contained" onClick={() => sendApiRequest()} >Run</Button>
    </div>
  </div>

  );
}

export default App;
