import './App.css';
import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import NavBar from './components/NavBar';
import Box from '@material-ui/core/Box';
import Icon from '@material-ui/core/Icon';
import { createMuiTheme } from '@material-ui/core/styles';
import { ThemeProvider } from '@material-ui/styles';

const theme = createMuiTheme({
  palette: {
    primary: {
      //light: '#757ce8',
      main: '#00695f',
      //dark: '#002884',
      //contrastText: '#fff',
    },
    secondary: {
      //light: '#ff7961',
      main: '#1c54b2',
      //dark: '#ba000d',
      //contrastText: '#000',
    },
  },
});

const useStyles = makeStyles((theme) => ({
  root: {
    '& .MuiTextField-root': {
      margin: theme.spacing(1),
      width: '60ch',
    },
  },
}));

const useStyles1 = makeStyles((theme) => ({
  root: {
    '& .MuiTextField-root': {
      margin: theme.spacing(1),
      width: '70ch',
      //top: -10,
      //left: 700
      //background: 'linear-gradient(45deg, #FE6B8B 30%, #FF8E53 90%)',
    },
  },
}));

const useStylesbutton = makeStyles((theme) => ({
  root: {
    '& > *': {
      margin: theme.spacing(1),
      //left: 565,
      top: 15,
      width: '13ch',
    },
  },
}));



function App() {

  const classes = useStyles();
  const classes1 = useStyles1();
  const classes2 = useStylesbutton();
  // eslint-disable-next-line
  const [value, setValue] = React.useState('Controlled');
  const [inputvalue, setInputValue] = React.useState();
  const [outputvalue, setOuputValue] = React.useState();
  // eslint-disable-next-line
  const handleChange = (event) => {
    setValue(event.target.value);
  };

   function sendApiRequest() {
  //   const requestOptions = {
  //     method: 'POST',
  //     headers: { 'Content-Type': 'application/json' },
  //     body: JSON.stringify({ cmd: inputvalue })
  //     };
  //   fetch('http://192.168.62.22:5000/listhellow/', requestOptions)
  //   .then(response => response.json())
  //   .then(data => setOuputValue(data.output));

    setOuputValue(inputvalue);

  }

  return (

    <div>
      <ThemeProvider theme={theme}>
    </ThemeProvider>
      <link rel="stylesheet" href="https://fonts.googleapis.com/icon?family=Material+Icons" />
      <div>
        <NavBar />
      </div>

        <Box display="flex" flexDirection="row" p={1} m={1} bgcolor="background.paper">
          <Box p={1}>
            <form className={classes.root} noValidate autoComplete="off">
            <TextField
            id="filled-textarea"
            label="Input Code"
            InputLabelProps={{ shrink: true }}
            placeholder="ZOT Code"
            multiline
            variant="filled"
            type= "text"
            value={inputvalue} 
            onChange={(e) => setInputValue(e.target.value)}
            />
            </form>
          </Box>
          <Box display="flex" flexDirection="column" p={1} m={1} bgcolor="background.paper">
          <Box p={1} >
            <div className={classes2.root}>
            <Button 
              variant="contained" 
              size='medium' 
              color="primary" 
              onClick={() => sendApiRequest()} 
              endIcon={<Icon>play_circle</Icon>}
              >
              Run
              </Button>
            </div>
          </Box>

          <Box p={1} >
            <div className={classes2.root}>
            <Button 
              variant="contained" 
              size= 'medium' 
              color="secondary" 
              onClick={() => sendApiRequest()} 
              endIcon={<Icon>sim_card_download</Icon>}
              >
              Docs
              </Button>
            </div>
          </Box>
          </Box>
          <Box p={1} >
            <form className={classes1.root} noValidate autoComplete="off" >
            <TextField
            id="filled-textarea1"
            //disabled={true}
            InputLabelProps={{ shrink: true }}
            label = "Output Code"
            //placeholder="Placeholder 1"
            multiline
            variant="filled"
            type="text"
            value={outputvalue} 
            onChange={(e) => setOuputValue(e.target.value)}
            />
            </form>
          </Box>
      </Box>

  </div>

  );
}

export default App;
